//import NProgress from 'nprogress'
  import { test} from '../../api/api';
  import axios from "axios";
  export default {
      data() {
          return {
              globalConfig:[],
              type: [{value: 'config', label: '配置'},
                  {value: 'tag', label: '标签'}],
              filters: {
                  name: ''
              },
              total: 0,
              page: 1,
              pages: 0,
              listLoading: false,
              sels: [],//列表选中列
              editForm:{
                  data: {
                      id: '',
                      name: '',
                      type: 'config',
                      value: '',
                      description: ''
                  },
                  visible: false,
                  loading: false,
                  rules: {
                      name: [
                          { required: true, message: '请输入名称', trigger: 'blur' },
                          { min: 1, max: 50, message: '长度在 1 到 50 个字符', trigger: 'blur' }
                      ],
                      type : [
                          { required: true, message: '请选择类型', trigger: 'blur'}
                      ],
                      value: [
                          { required: true, message: '请输入值', trigger: 'blur' },
                      ],
                      description: [
                          { required: false, message: '请输入描述', trigger: 'blur' },
                          { max: 1024, message: '不能超过1024个字符', trigger: 'blur' }
                      ]
                  },
              },
          }
      },
      methods: {
          // 获取HOST列表
          getGlobalConfig() {
              this.listLoading = true;
              let self = this;
              let params = {
                  page: self.page,
                  name: self.filters.name,
                  type: self.filters.type,
              };
              let headers = {
                  Authorization: 'Token '+sessionStorage.getItem('token')
              };
              axios.get(`${test}/api/global/config_list`, { params: params, headers:headers}).then(res => {
                  let { msg, code, data } = res.data;
                  self.listLoading = false;
                  if (code === '999999') {
                      self.total = data.total;
                      self.globalConfig = data.data;
                      self.pages=data.pages;
                  }
                  else {
                      self.$message.error({
                          message: msg,
                          center: true,
                      })
                  }
              });
          },
          handleSearch(){
              this.page=1;
              this.getGlobalConfig();
          },
          //删除
          handleDel: function (index, row) {
              this.$confirm('确认删除配置[' + row.name + ']吗?', '提示', {
                  type: 'warning'
              }).then(() => {
                  this.listLoading = true;
                  //NProgress.start();
                  let self = this;
                  let params = {
                      ids: [row.id, ]
                  };
                  let headers = {
                      "Content-Type": "application/json",
                      Authorization: 'Token '+sessionStorage.getItem('token')
                  };
                  axios.post(`${test}/api/global/del_config`, params, {headers}).then(res => {
                      let { msg, code, data } = res.data;
                      if (code === '999999') {
                          self.$message({
                              message: '删除成功',
                              center: true,
                              type: 'success'
                          })
                      } else {
                          self.$message.error({
                              message: msg,
                              center: true,
                          })
                      }
                      self.getGlobalConfig()
                  });
              }).catch(() => {
              });
          },
          handleChangeStatus: function(index, row) {
              let self = this;
              this.listLoading = true;
              let params = {
                  config_id: Number(row.id)
              };
              let headers = {
                  "Content-Type": "application/json",
                  Authorization: 'Token '+sessionStorage.getItem('token')
              };
              if (row.status) {
                  axios.post(`${test}/api/global/disable_config`, params, {headers}).then(res => {
                      let {msg, code, data} = res.data;
                      self.listLoading = false;
                      if (code === '999999') {
                          self.$message({
                              message: '禁用成功',
                              center: true,
                              type: 'success'
                          });
                          row.status = !row.status;
                      }
                      else {
                          self.$message.error({
                              message: msg,
                              center: true,
                          })
                      }
                  });
              } else {
                  axios.post(`${test}/api/global/enable_config`, params, {headers}).then(res => {
                      let {msg, code, data} = res.data;
                      self.listLoading = false;
                      if (code === '999999') {
                          self.$message({
                              message: '启用成功',
                              center: true,
                              type: 'success'
                          });
                          row.status = !row.status;
                      }
                      else {
                          self.$message.error({
                              message: msg,
                              center: true,
                          })
                      }
                  });
              }
          },
          handleCurrentChange(val) {
              this.page = val;
              this.getGlobalConfig()
          },
          //显示编辑界面
          handleEdit: function (index, row) {
              this.editForm.title="编辑";
              this.editForm.visible = true;
              this.editForm.data = Object.assign({}, row);
          },
          //显示新增界面
          handleAdd: function () {
              this.editForm.title="新增";
              this.editForm.visible = true;
              this.editForm.data = {
                      id: '',
                      name: '',
                      type: 'config',
                      value: '',
                      description: ''
                  };
          },
          //编辑
          editSubmit: function () {
              let self = this;
              this.$refs.editForm.validate((valid) => {
                  if (valid) {
                      this.$confirm('确认提交吗？', '提示', {}).then(() => {
                          self.editForm.loading = true;
                          //NProgress.start();
                          let params = {
                              name: self.editForm.data.name,
                              type: self.editForm.data.type,
                              value: self.editForm.data.value,
                              description: self.editForm.data.description
                          };
                          let url=`${test}/api/global/add_config`;
                          if(self.editForm.data.id!=""){
                              params["id"]=Number(self.editForm.data.id);
                              url=`${test}/api/global/update_config`;
                          }
                          let headers = {
                              "Content-Type": "application/json",
                              Authorization: 'Token '+sessionStorage.getItem('token')
                          };
                          axios.post(url, params, {headers}).then(res => {
                              let {msg, code, data} = res.data;
                              self.editForm.loading = false;
                              if (code === '999999') {
                                  self.$message({
                                      message: '执行成功',
                                      center: true,
                                      type: 'success'
                                  });
                                  self.$refs['editForm'].resetFields();
                                  self.editForm.visible = false;
                                  self.getGlobalConfig()
                              } else if (code === '999997'){
                                  self.$message.error({
                                      message: msg,
                                      center: true,
                                  })
                              } else {
                                  self.$message.error({
                                      message: msg,
                                      center: true,
                                  })
                              }
                          })
                      }).catch(() => {});
                  }
              });
          },
          selsChange: function (sels) {
              this.sels = sels;
          },
          //批量删除
          batchRemove: function () {
              let ids = this.sels.map(item => item.id);
              let self = this;
              this.$confirm('确认删除选中记录吗？', '提示', {
                  type: 'warning'
              }).then(() => {
                  self.listLoading = true;
                  //NProgress.start();
                  let params = {
                      ids: ids
                  };
                  let headers = {
                      "Content-Type": "application/json",
                      Authorization: 'Token ' + sessionStorage.getItem('token')
                  };
                  axios.post(`${test}/api/global/del_config`, params, {headers}).then(res => {
                      let {msg, code, data} = res.data;
                      self.listLoading = false;
                      if (code === '999999') {
                          self.$message({
                              message: '删除成功',
                              center: true,
                              type: 'success'
                          })
                      }
                      else {
                          self.$message.error({
                              message: msg,
                              center: true,
                          })
                      }
                      self.getGlobalConfig()
                  })
              }).catch(() => {});
          }
      },
      mounted() {
          this.getGlobalConfig();
      }
  }
